mapper.select:
select r.id as id, r.written_user as writtenUser, u.nickname as memberNick, r.content as content, r.non_mem_nick as nonMemNick
from reply r
    join user u on r.written_user = u.account_id
where post_id = #{postId}
order by r.written_time desc
limit #{limit}
offset #{offset}

select id, post_id as postId, content, written_user as writtenUser, non_mem_nick as nonMemNick, non_mem_pw as nonMemPw
from reply
where id = #{id}
